const WORKING_DIR = "/tmp";

// This project has to be created
const PROJECT_ID = kaholo.execution.configuration.projectId;
const SECRET_KEY = kaholo.execution.configuration.gcpKey;

// Make sure the bucket name is unique
const BUCKET_NAME = kaholo.execution.configuration.bucket.name;

// This is the storage class of the GCP Cloud Storage bucket
const STORAGE_CLASS = kaholo.execution.configuration.bucket.storageClass;

// Location of the GCP bucket
const LOCATION = kaholo.execution.configuration.bucket.location;

const LOGS_PATH = kaholo.execution.configuration.localLogsPath;

const INSTANCE_TYPE = kaholo.execution.configuration.instance.type;

const REMOTE_LOGS_PATH = kaholo.execution.configuration.instance.remoteLogsPath;

const INSTANCE_USER = kaholo.execution.configuration.instance.user;

const INSTANCE_ZONE = kaholo.execution.configuration.instance.zone;

const INSTANCE_HOST = kaholo.execution.configuration.instance.host;

const AWS_SECRET_KEY = kaholo.execution.configuration.instance.secretKey;

async function get_key() {
    return await kaholo.vault.getValueByKey(SECRET_KEY);
}

async function import_gcp_profile() {
    const KEY = await kaholo.vault.getValueByKey(SECRET_KEY);
    return `echo '${KEY}' > key.json; gcloud auth activate-service-account --key-file key.json`;
}

function set_project() {
    return `gcloud config set project ${PROJECT_ID}`
}

function upload_files() {
    return `gsutil cp -r '${LOGS_PATH}/*.log' 'gs://${BUCKET_NAME}/${LOGS_PATH}'`
}

async function scp(){
    switch (INSTANCE_TYPE) {
        case "ec2":
            const AWS_KEY = await kaholo.vault.getValueByKey(AWS_SECRET_KEY);
            return `echo '${AWS_KEY}' > key.pem;chmod 400 key.pem;scp -o StrictHostKeyChecking=no -i key.pem -r ${INSTANCE_USER}@${INSTANCE_HOST}:${REMOTE_LOGS_PATH} ${LOGS_PATH}`
        case "ce":
            import_gcp_profile()
            return `gcloud compute scp --recurse ${INSTANCE_USER}@${INSTANCE_HOST}:${REMOTE_LOGS_PATH} ${LOGS_PATH} --zone ${INSTANCE_ZONE}`
    }
}
