function getInstanceId() {
    result = actions.getInstance.result.describeInstances.Reservations[0].Instances[0].InstanceId
    return result
}

function getVolumeId() {
    result = actions.getInstance.result.describeInstances.Reservations[0].Instances[0].BlockDeviceMappings[0].Ebs.VolumeId
    return result
}

function getGroupId() {
    result = actions.createGroup.result.createSecurityGroup.GroupId
    return result
}

async function createConfig() {
    lines = ['[default]', `${await kaholo.vault.getValueByKey("AWS Access Key ID")}`, `${await kaholo.vault.getValueByKey("AWS Secret Access Key")}`]
    return `echo '${lines[0]}' > credentials; echo 'aws_access_key_id = ${lines[1]}' >> credentials; echo 'aws_secret_access_key = ${lines[2]}' >> credentials`
}

function attachRules() {
    instanceId = actions.getInstance.result.describeInstances.Reservations[0].Instances[0].InstanceId
    groupId = actions.createGroup.result.createSecurityGroup.GroupId
    return `aws ec2 modify-instance-attribute --instance-id ${instanceId} --groups ${groupId}`
}

function getLogs() {
    user = kaholo.execution.configuration.USERNAME
    host = kaholo.execution.configuration.HOSTNAME
    return `eval \`ssh-agent\`; ssh-add .ssh/key; rsync -e "ssh -o StrictHostKeyChecking=no" -avzp ${user}@${host}:/home/ubuntu/logs/ /root/logs/`
}

function uploadLogsPath() {
    utc = new Date().toJSON().slice(0,16);
    return `${utc}_logs`
}

function pagerTitle() {
    title = `Your instance has been quarantined. Logs: ${actions.uploadLogs.result.Location}`
    return title
}
